import React from "react";
import ReactDOM from "react-dom/client";
import "./index.css";
// import App from "./App";
import reportWebVitals from "./reportWebVitals";
import {
  createBrowserRouter,
  redirect,
  RouterProvider,
} from "react-router-dom";
import Root from "./routes/Root";
import Index from "./routes/Index";
import {
  fetchParkingLots,
  fetchParklingLotComment,
  fetchParklingLotDetail,
  saveRating,
} from "./api";
import "bootstrap/dist/css/bootstrap.css";
import Detail from "./routes/Detail";
import LeaveRating from "./routes/LeaveRating";
import { toast } from "react-toastify";
import RatingPage from "./routes/RatingPage";
import CommentPage from "./routes/CommentPage";

const router = createBrowserRouter([
  {
    path: "/",
    element: <Root />,
    children: [
      {
        path: "/",
        element: <Index />,
        loader() {
          return fetchParkingLots();
        },
      },
      {
        path: "/parkingLots/:id",
        loader({ params }) {
          return fetchParklingLotDetail(params.id);
        },
        element: <Detail />,
        children: [
          {
            path: "/parkingLots/:id/ratings",
            loader({ params }) {
              return fetchParklingLotDetail(params.id);
            },
            element: <RatingPage />,
            children: [
              {
                path: "/parkingLots/:id/ratings/comments",
                loader({ params }) {
                  return fetchParklingLotComment(params.id);
                },
                element: <CommentPage />,
              },
            ],
          },
          {
            path: "/parkingLots/:id/ratings/new",
            element: <LeaveRating />,
            loader({ params }) {
              return fetchParklingLotDetail(params.id);
            },
            action({ request, params }) {
              return request.formData().then((formData) => {
                // console.log(parseInt(formData.get("rating")));
                return saveRating(
                  params.id,
                  parseFloat(formData.get("origrating")),
                  parseInt(formData.get("numrating")),
                  parseInt(formData.get("rating"))
                ).then(() => {
                  toast.success("You have successfully submitted the rating.");
                  return redirect(`/parkingLots/${params.id}/ratings`);
                });
              });
            },
          },
        ],
      },
    ],
  },
]);

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<RouterProvider router={router} />);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
