import { faStar } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useState } from "react";
import { Form, useLoaderData } from "react-router-dom";
import Rating from "../Rating";

export default function LeaveRating() {
  const [rating, setRating] = useState(3);

  const currNumRating = useLoaderData().NumRating;
  //   console.log(currNumRating);

  function handleChange(event) {
    console.log(event.target.value);
  }

  return (
    <Form method="post">
      <Rating rating={rating}>
        {(isFilled, starNumnber) => {
          const color = isFilled ? "#fcad03" : "#ddd";
          const size = "2x";
          // this is the actual button star
          return (
            <button
              type="button"
              className="btn btn-link"
              onClick={() => {
                setRating(starNumnber);
              }}
            >
              <FontAwesomeIcon icon={faStar} color={color} size={size} />
            </button>
          );
        }}
      </Rating>
      <input
        type="text"
        value={rating}
        onChange={handleChange}
        name="rating"
        className="hidden"
      />
      <input
        type="text"
        value={currNumRating}
        onChange={handleChange}
        name="numrating"
        className="hidden"
      />
      <button type="submit" className="btn btn-primary">
        Submit
      </button>
    </Form>
  );
}
