import { createPortal } from "react-dom";
import "./Modal.css";

export default function Modal(props) {
  return createPortal(
    <div className="custom-modal">
      <h5>Are you sure to delete?</h5>
      <button
        type="button"
        onClick={() => {
          props.onClose();
        }}
      >
        Cancel
      </button>
      <button type="submit">Confirm</button>
    </div>,
    document.getElementById("modal-container")
  );
}
