import { useEffect, useState } from "react";
import { Form, useLoaderData } from "react-router-dom";
import Checkbox from "./Checkbox";
import Modal from "./Modal";

export default function Admin() {
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    document.title = "Parkable: Admin";
  }, []);

  const data = [{ id: "Comment 0" }, { id: "Comment 1" }, { id: "Comment 2" }];

  const datas = useLoaderData();
  const newdata = datas.map((data) => {
    return "id:" + data.id + ", " + data.body + " for " + data.parkingLotId;
  });
  console.log(newdata);

  const [parkingLots, setParkingLots] = useState([]);

  useEffect(() => {
    setParkingLots(data);
  }, []);

  const handleChange = (event) => {
    const { id, checked } = event.target;
    if (id === "selectAll") {
      let tempParkingLot = parkingLots.map((parkingLot) => {
        return { ...parkingLot, isChecked: checked };
      });
      setParkingLots(tempParkingLot);
    } else {
      let tempParkingLot = parkingLots.map((parkingLot) =>
        parkingLot.id === id
          ? { ...parkingLot, isChecked: checked }
          : parkingLot
      );
      setParkingLots(tempParkingLot);
    }
  };

  return (
    <div>
      <button
        type="button"
        id="menu"
        onClick={() => {
          setIsModalOpen(!isModalOpen);
        }}
      >
        Menu
      </button>

      {isModalOpen && (
        <Modal
          onClose={() => {
            setIsModalOpen(!isModalOpen);
          }}
        ></Modal>
      )}

      <Form>
        <Checkbox parkingLots={parkingLots} handleChange={handleChange} />
        <button type="submit" className="btn btn-sm btn-danger">
          Delete
        </button>
      </Form>
    </div>
  );
}
