import { useState } from "react";
import Modal from "./Modal";

export default function Admin() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  return (
    <div>
      <button
        type="button"
        id="menu"
        onClick={() => {
          setIsModalOpen(!isModalOpen);
        }}
      >
        Menu
      </button>

      {isModalOpen && (
        <Modal
          onClose={() => {
            setIsModalOpen(!isModalOpen);
          }}
        ></Modal>
      )}
    </div>
  );
}
