import React, { useState } from "react";
import GoogleMapReact from "google-map-react";
import Marker from "./Marker";
import Card from "./Card";
import { useLoaderData } from "react-router-dom";

export default function Map() {
  const [activePoint, setActivePoint] = useState();

  const points = useLoaderData();

  const defaultProps = {
    center: {
      lat: 34.052235,
      lng: -118.243683,
    },
    zoom: 11,
  };

  // const points = [
  //   { id: 1, title: "Round Pond", lat: 34.152235, lng: -118.243683 },
  //   { id: 2, title: "The Long Water", lat: 34.252235, lng: -118.343683 },
  //   { id: 3, title: "The Serpentine", lat: 34.352235, lng: -118.443683 },
  // ];

  return (
    <div>
      <div
        style={{
          position: "absolute",
          left: 0,
          top: 0,
          width: "30%",
          height: "100%",
        }}
      >
        {points.map((point) => {
          return (
            <Card
              key={point.id}
              title={point.title}
              onClick={() => {
                // console.log("BOOM", point);
                setActivePoint(point);
              }}
              isActive={point.id === activePoint?.id}
            />
          );
        })}
      </div>
      <div
        style={{
          position: "absolute",
          right: 0,
          top: 0,
          width: "70%",
          height: "100%",
        }}
      >
        <GoogleMapReact
          bootstrapURLKeys={{ key: "AIzaSyD_uy502Rcw9ljm2QquhymBPjqHoA4LOM4" }}
          defaultCenter={defaultProps.center}
          defaultZoom={defaultProps.zoom}
        >
          {points.map((point) => {
            const { lat, lng, id, title } = point;

            return (
              <Marker
                key={id}
                lat={lat}
                lng={lng}
                text={id}
                tooltip={title}
                onClick={() => {
                  // console.log("BOOM", point);
                  setActivePoint(point);
                }}
                isActive={point.id === activePoint?.id}
              />
            );
          })}
        </GoogleMapReact>
      </div>
    </div>
  );
}
