import { faStar } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useState } from "react";
import { Form } from "react-router-dom";
import Rating from "../Rating";

export default function LeaveRating() {
  const [rating, setRating] = useState(3);

  function handleChange() {
    return (
      <Rating rating={rating}>
        {(isFilled, starNumnber) => {
          const color = isFilled ? "#fcad03" : "#ddd";
          const size = "2x";
          // this is the actual button star
          return (
            <button
              type="button"
              className="btn btn-link"
              onClick={() => {
                setRating(starNumnber);
              }}
            >
              <FontAwesomeIcon icon={faStar} color={color} size={size} />
            </button>
          );
        }}
      </Rating>
    );
  }

  return (
    <Form method="post">
      <input type="number" value={rating} onChange={handleChange} />
      <button type="submit" className="btn btn-primary">
        Submit
      </button>
    </Form>
  );
}
